package org.usfirst.frc3932.commands;
import edu.wpi.first.wpilibj.command.CommandGroup;

/**
 * Loads a tote from the floor into the magazine
 * Opens the gate 
 * Raises elevator to hang position
 * Closes the gate
 * Closes the tab
 * Resets arems to "look for tote position"
 */
public class HangTote extends CommandGroup {
    
    public  HangTote() {
        addParallel(new GatesFullOpen());
        addSequential(new ElevatorToHang());
        addSequential(new WaitFor(1));
        addParallel(new DisableToteTabSafety());
        addParallel(new GatesClosed());
        addSequential(new ToteTabsClose());
        addSequential(new LookForTote());
        addSequential(new EnableToteTabSafety());
    }
}
